mapper.select:
SELECT issuekey, labels, title, status, assingee, issuedate, duedate FROM issuetable WHERE issuedate BETWEEN #{startDate} AND #{endDate}
AND labels LIKE CONCAT('%', #{label}, '%')
AND status = #{status}
ORDER BY issuedate DESC

SELECT
issuekey, labels, title, status,
assingee, issuedate, duedate
FROM issuetable
WHERE issuedate BETWEEN #{startDate} AND #{endDate}

    AND labels LIKE CONCAT('%', #{label}, '%')


    AND status = #{status}

ORDER BY issuedate DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM issuetable
WHERE issuedate BETWEEN #{startDate} AND #{endDate}

    AND labels LIKE CONCAT('%', #{label}, '%')


    AND status = #{status}
